
public class SystemEx06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// System -> out -> print
		System.out.print("Hello");
		System.out.print("\n");
		System.out.print("World");
		
		System.out.println();
		
		System.out.print("Hello");
		System.out.print(System.lineSeparator());
		System.out.print("World");
		
		

	}

}
